CommonData.BannerCustomCSS:
/* Banner */
#onetrust-banner-sdk.otCenterRounded {
    font-size: initial;
}

#onetrust-consent-sdk button:not(.ot-close-icon):not(#filter-btn-handler) {
    height: 48px !important;
    padding-left: 16px !important;
    padding-right: 16px !important;
}

#onetrust-consent-sdk button:hover,
#onetrust-consent-sdk button:active,
#onetrust-consent-sdk button:focus {
    opacity: 1 !important;
}

#onetrust-consent-sdk button:not(.ot-close-icon):not(.cookie-setting-link):not(.ot-host-box):active,
#onetrust-consent-sdk button:not(.ot-close-icon):not(.cookie-setting-link):not(.ot-host-box):hover {
    background-color: #1c52bd !important;
    transition: background-color .2s linear;
}

#onetrust-pc-btn-handler {
    text-decoration: none !important;
}

#onetrust-pc-btn-handler:hover {
    text-decoration: underline !important;
}

/* Banner margins */
#onetrust-banner-sdk.otFlat #onetrust-policy {
    margin: 16px 0 0 0 !important;
}

#onetrust-banner-sdk.otCenterRounded #onetrust-policy-title {
    padding-bottom: 16px !important;
}

@media only screen and (min-width: 480px) {
    #onetrust-banner-sdk.otFlat #onetrust-policy {
        margin-left: 24px !important;
    }
    #onetrust-banner-sdk.otCenterRounded #onetrust-policy {
        margin-top: 16px !important;
    }
    #onetrust-banner-sdk.otCenterRounded #onetrust-policy-title {
        padding-left: 24px;
        padding-right: 24px;
        padding-bottom: 14px !important;
    }
    #onetrust-banner-sdk.otCenterRounded #onetrust-button-group-parent {
        padding: 0 24px;
    }
}

@media only screen and (min-width: 768px) and (max-width: 1024px) {
    #onetrust-banner-sdk.otFlat #onetrust-button-group-parent {
        padding-right: 16px;
        padding-bottom: 16px;
        text-align: right;
    }
    #onetrust-banner-sdk.otFlat #onetrust-button-group {
        margin-right: 0 !important;
    }
}

#onetrust-banner-sdk.otCenterRounded #onetrust-policy-text {
    margin-left: 0;
    margin-right: 0;
    padding: 24px;
    box-shadow: inset 0px 4px 3px -3px rgba(0,0,50,0.1);
}

#onetrust-banner-sdk.otFlat #onetrust-policy-title {
    line-height: 2 !important;
}

#onetrust-banner-sdk #onetrust-close-btn-container button {
    top: 26px !important;
}

/* Right align banner buttons */
@media only screen and (min-width: 480px) {
    #onetrust-consent-sdk .banner-actions-container {
        width: auto !important;
    }
}

/* Remove banner as LCP candidate */
@keyframes slide-up {
  0% {
    transform: translateY(110vh);
  }
  100% {
    transform: translateY(0vh);
  }
}
#onetrust-banner-sdk {
  transform: translateY(110vh);
  animation: slide-up 1s forwards;
}

